SELECT a.trade_date ,a.ts_code ,b.symbol ,b.name ,b.industry ,b.market ,a.open ,a.high ,a.low ,a.close ,a.vol ,a.amount FROM ( SELECT ts_code ,trade_date ,amount ,open ,high ,low ,close ,vol FROM daily WHERE id IN
#{id}
) AS a JOIN ( SELECT ts_code ,name ,industry ,symbol ,market FROM stock_basic WHERE market IN ('主板','创业板') ) AS b ON a.ts_code = b.ts_code ORDER BY a.amount DESC

SELECT a.trade_date, a.ts_code, b.symbol, b.name, b.industry, a.open, a.high, a.low, a.close, a.vol, a.amount, a.pre_close, a.change, a.pct_chg FROM ( SELECT * FROM daily WHERE trade_date BETWEEN #{begin_date,jdbcType=VARCHAR} AND #{end_date,jdbcType=VARCHAR}
AND ts_code = #{ts_code,jdbcType=VARCHAR}
) AS a INNER JOIN ( SELECT ts_code ,name ,industry ,symbol FROM stock_basic WHERE market IN ('主板','创业板')
AND name = #{name,jdbcType=VARCHAR}
AND industry = #{industry,jdbcType=VARCHAR}
AND symbol = #{symbol,jdbcType=VARCHAR}
) AS b ON a.ts_code = b.ts_code ORDER BY a.trade_date DESC, a.ts_code DESC, a.amount DESC

SELECT a.trade_date, a.ts_code, b.symbol, b.name, b.industry, a.open, a.high, a.low, a.close, a.vol, a.amount, a.pre_close, a.change, a.pct_chg FROM ( SELECT * FROM stock_hfq_daily WHERE trade_date BETWEEN #{begin_date,jdbcType=VARCHAR} AND #{end_date,jdbcType=VARCHAR}
AND ts_code = #{ts_code,jdbcType=VARCHAR}
) AS a INNER JOIN ( SELECT ts_code ,name ,industry ,symbol FROM stock_basic WHERE market IN ('主板','创业板')
AND name = #{name,jdbcType=VARCHAR}
AND industry = #{industry,jdbcType=VARCHAR}
) AS b ON a.ts_code = b.ts_code ORDER BY a.trade_date DESC, a.ts_code DESC, a.amount DESC
